import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaBook, FaShoppingCart, FaUser, FaSignOutAlt, FaSearch, FaHistory, FaBox, FaBookOpen, FaUserShield } from 'react-icons/fa';
import './Navbar.css';
import axios from 'axios';

const Navbar = ({ isAuthenticated, user, onLogout }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const navigate = useNavigate();

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/search?q=${encodeURIComponent(searchQuery.trim())}`);
      setSearchQuery('');
    }
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const handleLogout = () => {
    onLogout();
    navigate('/');
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <div className="navbar-brand">
          <Link to="/" className="logo">
            <FaBook className="logo-icon" />
            <span>BookStore</span>
          </Link>
        </div>

        <div className="search-container">
          <form onSubmit={handleSearch} className="search-form">
            <input
              type="text"
              placeholder="Search books..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="search-input"
            />
            <button type="submit" className="search-button">
              <FaSearch />
            </button>
          </form>
        </div>

        <div className="nav-links">
          <Link to="/books" className="nav-link">
            <FaBookOpen />
            <span>All Books</span>
          </Link>
          {isAuthenticated ? (
            <>
              <Link to="/cart" className="nav-link cart-link">
                <FaShoppingCart />
                <span>Cart</span>
              </Link>
              {user?.role === 'admin' && (
                <Link to="/admin" className="nav-link admin-link">
                  <FaUserShield />
                  <span>Admin Panel</span>
                </Link>
              )}
              <div className="user-dropdown">
                <button className="user-button" onClick={toggleDropdown}>
                  <FaUser />
                  <span>Account</span>
                </button>
                {isDropdownOpen && (
                  <div className="dropdown-menu">
                    <Link to="/profile" className="dropdown-item">
                      <FaUser /> Profile
                    </Link>
                    <Link to="/orders" className="dropdown-item">
                      <FaHistory /> Order History
                    </Link>
                    <Link to="/tracking" className="dropdown-item">
                      <FaBox /> Track Orders
                    </Link>
                    {user?.role === 'admin' && (
                      <>
                        <Link to="/admin" className="dropdown-item">
                          <FaUserShield /> Admin Dashboard
                        </Link>
                        <Link to="/admin/books" className="dropdown-item">
                          <FaBook /> Manage Books
                        </Link>
                      </>
                    )}
                    <button onClick={handleLogout} className="dropdown-item">
                      <FaSignOutAlt /> Logout
                    </button>
                  </div>
                )}
              </div>
            </>
          ) : (
            <>
              <Link to="/login" className="nav-link">Login</Link>
              <Link to="/register" className="nav-link">Register</Link>
            </>
          )}
        </div>

        <button className="mobile-menu-button" onClick={toggleMenu}>
          <span className={`hamburger ${isMenuOpen ? 'open' : ''}`}></span>
        </button>
      </div>

      {isMenuOpen && (
        <div className="mobile-menu">
          <form onSubmit={handleSearch} className="mobile-search">
            <input
              type="text"
              placeholder="Search books..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button type="submit">
              <FaSearch />
            </button>
          </form>
          <Link to="/books" className="mobile-link">
            <FaBookOpen /> All Books
          </Link>
          {isAuthenticated ? (
            <>
              <Link to="/cart" className="mobile-link">
                <FaShoppingCart /> Cart
              </Link>
              {user?.role === 'admin' && (
                <>
                  <Link to="/admin" className="mobile-link">
                    <FaUserShield /> Admin Panel
                  </Link>
                  <Link to="/admin/books" className="mobile-link">
                    <FaBook /> Manage Books
                  </Link>
                </>
              )}
              <Link to="/profile" className="mobile-link">
                <FaUser /> Profile
              </Link>
              <Link to="/orders" className="mobile-link">
                <FaHistory /> Order History
              </Link>
              <Link to="/tracking" className="mobile-link">
                <FaBox /> Track Orders
              </Link>
              <button onClick={handleLogout} className="mobile-link">
                <FaSignOutAlt /> Logout
              </button>
            </>
          ) : (
            <>
              <Link to="/login" className="mobile-link">Login</Link>
              <Link to="/register" className="mobile-link">Register</Link>
            </>
          )}
        </div>
      )}
    </nav>
  );
};

export default Navbar;
