import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { 
  FaFilter, 
  FaTimes, 
  FaShoppingCart, 
  FaSearch, 
  FaSort, 
  FaSortAmountDown, 
  FaSortAmountUp 
} from 'react-icons/fa';
import './Books.css';

const Books = () => {
  const [books, setBooks] = useState([]);
  const [filteredBooks, setFilteredBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showFilters, setShowFilters] = useState(false);
  const [addingToCart, setAddingToCart] = useState({});
  const [sortOrder, setSortOrder] = useState('default'); // default, price-asc, price-desc, title-asc, title-desc
  const [filters, setFilters] = useState({
    category: '',
    minPrice: '',
    maxPrice: '',
    searchQuery: ''
  });

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/books');
        setBooks(response.data);
        setFilteredBooks(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to load books. Please try again later.');
        setLoading(false);
      }
    };

    fetchBooks();
  }, []);

  useEffect(() => {
    let result = [...books];

    // Apply category filter
    if (filters.category) {
      result = result.filter(book => book.category === filters.category);
    }

    // Apply price range filter
    if (filters.minPrice) {
      result = result.filter(book => book.price >= parseFloat(filters.minPrice));
    }
    if (filters.maxPrice) {
      result = result.filter(book => book.price <= parseFloat(filters.maxPrice));
    }

    // Apply search query
    if (filters.searchQuery) {
      const query = filters.searchQuery.toLowerCase();
      result = result.filter(
        book =>
          book.title.toLowerCase().includes(query) ||
          book.author.toLowerCase().includes(query) ||
          book.description.toLowerCase().includes(query)
      );
    }

    // Apply sorting
    switch (sortOrder) {
      case 'price-asc':
        result.sort((a, b) => a.price - b.price);
        break;
      case 'price-desc':
        result.sort((a, b) => b.price - a.price);
        break;
      case 'title-asc':
        result.sort((a, b) => a.title.localeCompare(b.title));
        break;
      case 'title-desc':
        result.sort((a, b) => b.title.localeCompare(a.title));
        break;
      default:
        // Keep original order
        break;
    }

    setFilteredBooks(result);
  }, [filters, books, sortOrder]);

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSortChange = (e) => {
    setSortOrder(e.target.value);
  };

  const clearFilters = () => {
    setFilters({
      category: '',
      minPrice: '',
      maxPrice: '',
      searchQuery: ''
    });
    setSortOrder('default');
  };

  const categories = [...new Set(books.map(book => book.category))];

  const handleAddToCart = async (bookId) => {
    try {
      setAddingToCart(prev => ({ ...prev, [bookId]: true }));
      const token = localStorage.getItem('token');
      
      if (!token) {
        window.location.href = '/login';
        return;
      }

      const response = await axios.post(
        'http://localhost:5001/api/cart',
        { bookId, quantity: 1 },
        {
          headers: {
            'x-auth-token': token,
            'Content-Type': 'application/json'
          }
        }
      );

      // Show success message
      const successMessage = document.createElement('div');
      successMessage.className = 'success-message';
      successMessage.textContent = 'Book added to cart successfully!';
      document.body.appendChild(successMessage);
      setTimeout(() => {
        successMessage.remove();
      }, 3000);

    } catch (err) {
      console.error('Error adding to cart:', err);
      setError(err.response?.data?.message || 'Failed to add item to cart');
    } finally {
      setAddingToCart(prev => ({ ...prev, [bookId]: false }));
    }
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="loading-spinner"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <p className="error-message">{error}</p>
      </div>
    );
  }

  return (
    <div className="books-page">
      <div className="books-header">
        <h1>Our Books Collection</h1>
        <div className="header-actions">
          <div className="sort-container">
            <FaSort className="sort-icon" />
            <select 
              value={sortOrder} 
              onChange={handleSortChange}
              className="sort-select"
            >
              <option value="default">Sort by</option>
              <option value="price-asc">Price: Low to High</option>
              <option value="price-desc">Price: High to Low</option>
              <option value="title-asc">Title: A to Z</option>
              <option value="title-desc">Title: Z to A</option>
            </select>
          </div>
          <button 
            className="filter-toggle-button"
            onClick={() => setShowFilters(!showFilters)}
          >
            {showFilters ? <FaTimes /> : <FaFilter />}
            {showFilters ? 'Hide Filters' : 'Show Filters'}
          </button>
        </div>
      </div>

      <div className={`filters-container ${showFilters ? 'show' : ''}`}>
        <div className="search-bar">
          <FaSearch className="search-icon" />
          <input
            type="text"
            name="searchQuery"
            value={filters.searchQuery}
            onChange={handleFilterChange}
            placeholder="Search by title, author, or description..."
            className="search-input"
          />
        </div>

        <div className="filters-grid">
          <div className="filter-group">
            <label>Category</label>
            <select
              name="category"
              value={filters.category}
              onChange={handleFilterChange}
              className="filter-select"
            >
              <option value="">All Categories</option>
              {categories.map(category => (
                <option key={category} value={category}>
                  {category}
                </option>
              ))}
            </select>
          </div>

          <div className="filter-group">
            <label>Price Range</label>
            <div className="price-inputs">
              <input
                type="number"
                name="minPrice"
                value={filters.minPrice}
                onChange={handleFilterChange}
                placeholder="Min"
                className="price-input"
                min="0"
                step="0.01"
              />
              <span>to</span>
              <input
                type="number"
                name="maxPrice"
                value={filters.maxPrice}
                onChange={handleFilterChange}
                placeholder="Max"
                className="price-input"
                min="0"
                step="0.01"
              />
            </div>
          </div>

          <button className="clear-filters-button" onClick={clearFilters}>
            Clear All Filters
          </button>
        </div>
      </div>

      <div className="books-grid">
        {filteredBooks.length > 0 ? (
          filteredBooks.map(book => (
            <div key={book._id} className="book-card">
              <div className="book-image-container">
                <img src={book.image} alt={book.title} className="book-image" />
                <div className="book-overlay">
                  <Link to={`/books/${book._id}`} className="view-details-button">
                    View Details
                  </Link>
                </div>
              </div>
              <div className="book-info">
                <h2 className="book-title">{book.title}</h2>
                <p className="book-author">by {book.author}</p>
                <p className="book-price">${book.price.toFixed(2)}</p>
                <button
                  className="add-to-cart-button"
                  onClick={() => handleAddToCart(book._id)}
                  disabled={addingToCart[book._id]}
                >
                  <FaShoppingCart />
                  {addingToCart[book._id] ? 'Adding...' : 'Add to Cart'}
                </button>
              </div>
            </div>
          ))
        ) : (
          <div className="no-books-message">
            <p>No books found matching your criteria.</p>
            <button className="clear-filters-button" onClick={clearFilters}>
              Clear Filters
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Books;
