import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import popularBooks from '../data/books';
import './Home.css';

const Home = () => {
  // Carousel state
  const [current, setCurrent] = useState(0);
  const total = popularBooks.length;

  const nextSlide = () => setCurrent((prev) => (prev + 1) % total);
  const prevSlide = () => setCurrent((prev) => (prev - 1 + total) % total);

  return (
    <div className="home">
      {/* Hero Section */}
      <section className="hero modern-hero">
        <div className="hero-bg-animation"></div>
        <div className="hero-content">
          <h1>Discover Your Next Favorite Book</h1>
          <p>Explore bestsellers, classics, and new releases. Dive into a world of stories and knowledge.</p>
          <div className="hero-cta-buttons">
            <Link to="/books" className="hero-button primary">Browse Books</Link>
            <Link to="/register" className="hero-button secondary">Join Now</Link>
          </div>
        </div>
      </section>

      {/* Featured Books Carousel */}
      <section className="featured-carousel">
        <h2>Featured Books</h2>
        <div className="carousel-container">
          <button className="carousel-arrow left" onClick={prevSlide}>&#8592;</button>
          <div className="carousel-slide">
            <img src={popularBooks[current].coverImage} alt={popularBooks[current].title} className="carousel-image" />
            <div className="carousel-info">
              <h3>{popularBooks[current].title}</h3>
              <p className="carousel-author">by {popularBooks[current].author}</p>
              <p className="carousel-desc">{popularBooks[current].description}</p>
              <p className="carousel-price">${popularBooks[current].price.toFixed(2)}</p>
              <Link to={`/books/${popularBooks[current].id}`} className="carousel-details">View Details</Link>
            </div>
          </div>
          <button className="carousel-arrow right" onClick={nextSlide}>&#8594;</button>
        </div>
        <div className="carousel-indicators">
          {popularBooks.map((_, idx) => (
            <span key={idx} className={`indicator${idx === current ? ' active' : ''}`} onClick={() => setCurrent(idx)}></span>
          ))}
        </div>
      </section>

      {/* Categories */}
      <section className="categories modern-categories">
        <h2>Browse by Category</h2>
        <div className="category-grid">
          <Link to="/books?category=fiction" className="category-card">Fiction</Link>
          <Link to="/books?category=non-fiction" className="category-card">Non-Fiction</Link>
          <Link to="/books?category=science" className="category-card">Science</Link>
          <Link to="/books?category=history" className="category-card">History</Link>
        </div>
      </section>
    </div>
  );
};

export default Home;
