import React, { createContext, useContext, useState, useEffect } from 'react';
import { useAuth } from './AuthContext';
import useApi from '../hooks/useApi';
import LoadingSpinner from '../components/LoadingSpinner';
import ErrorMessage from '../components/ErrorMessage';

const CartContext = createContext();

export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);
  const { isAuthenticated } = useAuth();
  const { loading, error, apiCall } = useApi();

  useEffect(() => {
    if (isAuthenticated) {
      fetchCart();
    } else {
      setCart([]);
    }
  }, [isAuthenticated]);

  const fetchCart = async () => {
    try {
      const cartData = await apiCall('get', '/cart');
      setCart(cartData);
    } catch (error) {
      console.error('Cart fetch error:', error);
    }
  };

  const addToCart = async (bookId, quantity = 1) => {
    const updatedCart = await apiCall('post', '/cart/add', { bookId, quantity });
    setCart(updatedCart);
    return updatedCart;
  };

  const removeFromCart = async (bookId) => {
    const updatedCart = await apiCall('delete', `/cart/remove/${bookId}`);
    setCart(updatedCart);
    return updatedCart;
  };

  const updateQuantity = async (bookId, quantity) => {
    const updatedCart = await apiCall('put', '/cart/update', { bookId, quantity });
    setCart(updatedCart);
    return updatedCart;
  };

  const clearCart = async () => {
    const emptyCart = await apiCall('delete', '/cart/clear');
    setCart(emptyCart);
    return emptyCart;
  };

  const getTotalItems = () => {
    return cart.reduce((total, item) => total + item.quantity, 0);
  };

  const getTotalPrice = () => {
    return cart.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const value = {
    cart,
    loading,
    error,
    addToCart,
    removeFromCart,
    updateQuantity,
    clearCart,
    getTotalItems,
    getTotalPrice
  };

  if (loading) {
    return <LoadingSpinner message="Loading cart..." />;
  }

  if (error) {
    return <ErrorMessage message={error} onRetry={fetchCart} />;
  }

  return (
    <CartContext.Provider value={value}>
      {children}
    </CartContext.Provider>
  );
};
