import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaSearch, FaFilter, FaSort, FaTimes } from 'react-icons/fa';
import BookCard from '../components/BookCard';
import './Books.css';

const Books = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [priceRange, setPriceRange] = useState({ min: '', max: '' });
  const [sortBy, setSortBy] = useState('newest');
  const [showFilters, setShowFilters] = useState(false);

  const categories = [
    'Fiction',
    'Non-Fiction',
    'Science',
    'History',
    'Biography',
    'Self-Help',
    'Business',
    'Technology'
  ];

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/api/books');
      setBooks(response.data);
      setError(null);
    } catch (err) {
      setError('Failed to fetch books. Please try again later.');
      console.error('Error fetching books:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  const handlePriceChange = (e, type) => {
    setPriceRange(prev => ({
      ...prev,
      [type]: e.target.value
    }));
  };

  const handleSortChange = (e) => {
    setSortBy(e.target.value);
  };

  const clearFilters = () => {
    setSelectedCategory('');
    setPriceRange({ min: '', max: '' });
    setSearchQuery('');
    setSortBy('newest');
  };

  const filteredBooks = books
    .filter(book => {
      const matchesSearch = book.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
                          book.author.toLowerCase().includes(searchQuery.toLowerCase());
      const matchesCategory = !selectedCategory || book.category === selectedCategory;
      const matchesPrice = (!priceRange.min || book.price >= Number(priceRange.min)) &&
                          (!priceRange.max || book.price <= Number(priceRange.max));
      return matchesSearch && matchesCategory && matchesPrice;
    })
    .sort((a, b) => {
      switch (sortBy) {
        case 'price-low':
          return a.price - b.price;
        case 'price-high':
          return b.price - a.price;
        case 'newest':
          return new Date(b.createdAt) - new Date(a.createdAt);
        default:
          return 0;
      }
    });

  return (
    <div className="books-page">
      <header className="books-header">
        <div className="search-container">
          <div className="search-box">
            <FaSearch className="search-icon" />
            <input
              type="text"
              className="search-input"
              placeholder="Search for books, authors..."
              value={searchQuery}
              onChange={handleSearch}
            />
          </div>
          <div className="header-actions">
            <div className="sort-container">
              <FaSort className="sort-icon" />
              <select className="sort-select" value={sortBy} onChange={handleSortChange}>
                <option value="newest">Newest First</option>
                <option value="price-low">Price: Low to High</option>
                <option value="price-high">Price: High to Low</option>
              </select>
            </div>
            <button 
              className="filter-toggle-button"
              onClick={() => setShowFilters(!showFilters)}
            >
              <FaFilter />
              Filters
            </button>
          </div>
        </div>
      </header>

      <div className="books-container">
        <aside className={`filters-sidebar ${showFilters ? 'show' : ''}`}>
          <div className="filters-header">
            <h3>Filters</h3>
            <button className="clear-filters" onClick={clearFilters}>
              Clear All
            </button>
          </div>

          <div className="filter-section">
            <h4>Categories</h4>
            <div className="category-list">
              {categories.map(category => (
                <label key={category} className="category-item">
                  <input
                    type="radio"
                    name="category"
                    checked={selectedCategory === category}
                    onChange={() => handleCategoryChange(category)}
                  />
                  {category}
                </label>
              ))}
            </div>
          </div>

          <div className="filter-section">
            <h4>Price Range</h4>
            <div className="price-inputs">
              <input
                type="number"
                className="price-input"
                placeholder="Min"
                value={priceRange.min}
                onChange={(e) => handlePriceChange(e, 'min')}
              />
              <span>-</span>
              <input
                type="number"
                className="price-input"
                placeholder="Max"
                value={priceRange.max}
                onChange={(e) => handlePriceChange(e, 'max')}
              />
            </div>
          </div>
        </aside>

        <main className="books-content">
          {loading ? (
            <div className="loading">
              <div className="loading-spinner" />
            </div>
          ) : error ? (
            <div className="error-container">
              <p className="error-message">{error}</p>
              <button onClick={fetchBooks}>Try Again</button>
            </div>
          ) : filteredBooks.length === 0 ? (
            <div className="no-books-message">
              <p>No books found matching your criteria.</p>
            </div>
          ) : (
            <div className="books-grid">
              {filteredBooks.map(book => (
                <BookCard key={book._id} book={book} />
              ))}
            </div>
          )}
        </main>
      </div>
    </div>
  );
};

export default Books;
