import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './OrderConfirmation.css';

const OrderConfirmation = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchOrder = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/login');
          return;
        }

        const response = await axios.get(
          `http://localhost:5001/api/orders/${id}`,
          {
            headers: {
              'x-auth-token': token
            }
          }
        );

        setOrder(response.data);
      } catch (error) {
        console.error('Error fetching order:', error);
        setError('Failed to load order details');
      } finally {
        setLoading(false);
      }
    };

    fetchOrder();
  }, [id, navigate]);

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading">Loading...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <div className="error-message">{error}</div>
        <button onClick={() => navigate('/')} className="home-button">
          Return to Home
        </button>
      </div>
    );
  }

  if (!order) {
    return (
      <div className="error-container">
        <div className="error-message">Order not found</div>
        <button onClick={() => navigate('/')} className="home-button">
          Return to Home
        </button>
      </div>
    );
  }

  return (
    <div className="order-confirmation">
      <div className="confirmation-container">
        <div className="success-message">
          <h1>Order Confirmed!</h1>
          <p>Thank you for your purchase. Your order has been received.</p>
        </div>

        <div className="order-details">
          <h2>Order Details</h2>
          <div className="order-info">
            <p><strong>Order Number:</strong> {order._id}</p>
            <p><strong>Order Date:</strong> {new Date(order.orderDate).toLocaleDateString()}</p>
            <p><strong>Status:</strong> <span className={`status ${order.status}`}>{order.status}</span></p>
          </div>

          <div className="shipping-info">
            <h3>Shipping Information</h3>
            <p><strong>Name:</strong> {order.shippingAddress.fullName}</p>
            <p><strong>Address:</strong> {order.shippingAddress.address}</p>
            <p><strong>City:</strong> {order.shippingAddress.city}</p>
            <p><strong>State:</strong> {order.shippingAddress.state}</p>
            <p><strong>ZIP Code:</strong> {order.shippingAddress.zipCode}</p>
          </div>

          <div className="order-items">
            <h3>Order Items</h3>
            {order.items.map(item => (
              <div key={item._id} className="order-item">
                <img src={item.book.image} alt={item.book.title} />
                <div className="item-details">
                  <h4>{item.book.title}</h4>
                  <p>Quantity: {item.quantity}</p>
                  <p>Price: ${item.price}</p>
                </div>
                <p className="subtotal">${(item.price * item.quantity).toFixed(2)}</p>
              </div>
            ))}
          </div>

          <div className="order-total">
            <h3>Order Summary</h3>
            <div className="total-row">
              <span>Subtotal:</span>
              <span>${order.totalAmount.toFixed(2)}</span>
            </div>
            <div className="total-row">
              <span>Shipping:</span>
              <span>Free</span>
            </div>
            <div className="total-row grand-total">
              <span>Total:</span>
              <span>${order.totalAmount.toFixed(2)}</span>
            </div>
          </div>
        </div>

        <div className="action-buttons">
          <button onClick={() => navigate('/')} className="continue-shopping">
            Continue Shopping
          </button>
          <button onClick={() => navigate('/my-orders')} className="view-orders">
            View All Orders
          </button>
        </div>
      </div>
    </div>
  );
};

export default OrderConfirmation;
