import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import BookCard from '../components/BookCard';
import './Author.css';

const Author = () => {
  const { authorName } = useParams();
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [authorInfo, setAuthorInfo] = useState(null);

  useEffect(() => {
    const fetchAuthorBooks = async () => {
      try {
        const response = await axios.get(`/api/books/author/${encodeURIComponent(authorName)}`);
        setBooks(response.data.books);
        setAuthorInfo(response.data.authorInfo);
        setLoading(false);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to fetch author books');
        setLoading(false);
      }
    };

    fetchAuthorBooks();
  }, [authorName]);

  if (loading) {
    return (
      <div className="author-container">
        <div className="loading">Loading author's books...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="author-container">
        <div className="error">{error}</div>
      </div>
    );
  }

  return (
    <div className="author-container">
      <div className="author-header">
        <h1>{authorName}</h1>
        {authorInfo && (
          <div className="author-info">
            <div className="author-image">
              <img src={authorInfo.image} alt={authorName} />
            </div>
            <div className="author-bio">
              <p>{authorInfo.bio}</p>
              {authorInfo.website && (
                <a
                  href={authorInfo.website}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="author-website"
                >
                  Visit Author's Website
                </a>
              )}
            </div>
          </div>
        )}
      </div>

      <div className="books-section">
        <h2>Books by {authorName}</h2>
        {books.length === 0 ? (
          <div className="no-books">
            <p>No books found for this author.</p>
          </div>
        ) : (
          <div className="books-grid">
            {books.map((book) => (
              <BookCard key={book._id} book={book} />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Author;
