import React from 'react';
import { Link } from 'react-router-dom';
import { FaHeart, FaRegHeart, FaShoppingCart, FaStar, FaStarHalfAlt } from 'react-icons/fa';
import { useWishlist } from '../context/WishlistContext';
import { useCart } from '../context/CartContext';
import './BookCard.css';

const BookCard = ({ book }) => {
  const { isInWishlist, addToWishlist, removeFromWishlist } = useWishlist();
  const { addToCart } = useCart();
  const inWishlist = isInWishlist(book._id);

  const handleWishlistToggle = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (inWishlist) {
      removeFromWishlist(book._id);
    } else {
      addToWishlist(book);
    }
  };

  // Calculate discount percentage
  const discount = book.originalPrice ? Math.round(((book.originalPrice - book.price) / book.originalPrice) * 100) : 0;

  // Generate random rating between 3.5 and 5
  const rating = (Math.random() * 1.5 + 3.5).toFixed(1);
  const fullStars = Math.floor(rating);
  const hasHalfStar = rating % 1 !== 0;

  return (
    <div className="book-card">
      <div className="book-image-container">
        <img src={book.image} alt={book.title} className="book-image" />
        {discount > 0 && (
          <div className="discount-badge">
            {discount}% OFF
          </div>
        )}
        <button
          className={`wishlist-btn ${inWishlist ? 'active' : ''}`}
          onClick={handleWishlistToggle}
          title={inWishlist ? 'Remove from wishlist' : 'Add to wishlist'}
        >
          {inWishlist ? <FaHeart /> : <FaRegHeart />}
        </button>
      </div>
      
      <div className="book-details">
        <Link to={`/books/${book._id}`} className="book-title">
          {book.title}
        </Link>
        
        <p className="book-author">by {book.author}</p>
        
        <div className="rating-container">
          <div className="stars">
            {[...Array(fullStars)].map((_, i) => (
              <FaStar key={i} className="star" />
            ))}
            {hasHalfStar && <FaStarHalfAlt className="star" />}
          </div>
          <span className="rating-count">({Math.floor(Math.random() * 1000 + 100)})</span>
        </div>

        <div className="price-container">
          <div className="price-details">
            <span className="current-price">₹{book.price.toFixed(2)}</span>
            {book.originalPrice && (
              <span className="original-price">₹{book.originalPrice.toFixed(2)}</span>
            )}
            {discount > 0 && (
              <span className="discount-text">{discount}% off</span>
            )}
          </div>
          <button
            className="add-to-cart-btn"
            onClick={() => addToCart(book)}
          >
            <FaShoppingCart /> Add to Cart
          </button>
        </div>

        <div className="delivery-info">
          <span className="delivery-text">Free Delivery</span>
          <span className="delivery-time">Delivery by {new Date(Date.now() + 3 * 24 * 60 * 60 * 1000).toLocaleDateString()}</span>
        </div>
      </div>
    </div>
  );
};

export default BookCard;
