import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from './AuthContext';

const CartContext = createContext(null);

export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { isAuthenticated, user } = useAuth();

  useEffect(() => {
    if (isAuthenticated) {
      fetchCart();
    } else {
      setCart([]);
      setLoading(false);
    }
  }, [isAuthenticated]);

  const fetchCart = async () => {
    try {
      setError(null);
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5001/api/cart', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setCart(response.data);
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to fetch cart');
      console.error('Cart fetch error:', error);
    } finally {
      setLoading(false);
    }
  };

  const addToCart = async (bookId, quantity = 1) => {
    try {
      setError(null);
      const token = localStorage.getItem('token');
      const response = await axios.post(
        'http://localhost:5001/api/cart/add',
        { bookId, quantity },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setCart(response.data);
      return response.data;
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to add item to cart');
      throw error;
    }
  };

  const removeFromCart = async (bookId) => {
    try {
      setError(null);
      const token = localStorage.getItem('token');
      const response = await axios.delete(`http://localhost:5001/api/cart/remove/${bookId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setCart(response.data);
      return response.data;
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to remove item from cart');
      throw error;
    }
  };

  const updateQuantity = async (bookId, quantity) => {
    try {
      setError(null);
      const token = localStorage.getItem('token');
      const response = await axios.put(
        `http://localhost:5001/api/cart/update/${bookId}`,
        { quantity },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setCart(response.data);
      return response.data;
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to update cart');
      throw error;
    }
  };

  const clearCart = async () => {
    try {
      setError(null);
      const token = localStorage.getItem('token');
      await axios.delete('http://localhost:5001/api/cart/clear', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setCart([]);
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to clear cart');
      throw error;
    }
  };

  const getCartTotal = () => {
    return cart.reduce((total, item) => total + (item.book.price * item.quantity), 0);
  };

  const getCartCount = () => {
    return cart.reduce((count, item) => count + item.quantity, 0);
  };

  const value = {
    cart,
    loading,
    error,
    addToCart,
    removeFromCart,
    updateQuantity,
    clearCart,
    getCartTotal,
    getCartCount
  };

  return (
    <CartContext.Provider value={value}>
      {children}
    </CartContext.Provider>
  );
};
