import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './Home.css';

const Home = () => {
  const [featuredBooks, setFeaturedBooks] = useState([]);

  useEffect(() => {
    const fetchFeaturedBooks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/books');
        setFeaturedBooks(response.data.slice(0, 4)); // Get first 4 books
      } catch (error) {
        console.error('Error fetching featured books:', error);
      }
    };

    fetchFeaturedBooks();
  }, []);

  return (
    <div className="home">
      <section className="hero">
        <div className="hero-content">
          <h1>Welcome to BookStore</h1>
          <p>Discover your next favorite book</p>
          <Link to="/books" className="cta-button">
            Browse Books
          </Link>
        </div>
      </section>

      <section className="featured-books">
        <h2>Featured Books</h2>
        <div className="books-grid">
          {featuredBooks.map((book) => (
            <div key={book._id} className="book-card">
              <img src={book.image} alt={book.title} />
              <h3>{book.title}</h3>
              <p className="author">by {book.author}</p>
              <p className="price">${book.price}</p>
              <Link to={`/books/${book._id}`} className="view-button">
                View Details
              </Link>
            </div>
          ))}
        </div>
      </section>

      <section className="categories">
        <h2>Browse by Category</h2>
        <div className="category-grid">
          <Link to="/books?category=fiction" className="category-card">
            Fiction
          </Link>
          <Link to="/books?category=non-fiction" className="category-card">
            Non-Fiction
          </Link>
          <Link to="/books?category=science" className="category-card">
            Science
          </Link>
          <Link to="/books?category=history" className="category-card">
            History
          </Link>
        </div>
      </section>
    </div>
  );
};

export default Home;
