import React from 'react';
import { useWishlist } from '../context/WishlistContext';
import { useCart } from '../context/CartContext';
import { FaHeart, FaTrash, FaShoppingCart } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import './Wishlist.css';

const Wishlist = () => {
  const { wishlist, loading, error, removeFromWishlist } = useWishlist();
  const { addToCart } = useCart();

  if (loading) {
    return (
      <div className="wishlist-container">
        <div className="loading">Loading your wishlist...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="wishlist-container">
        <div className="error">{error}</div>
      </div>
    );
  }

  return (
    <div className="wishlist-container">
      <h1>My Wishlist</h1>
      {wishlist.length === 0 ? (
        <div className="empty-wishlist">
          <FaHeart className="empty-icon" />
          <h2>Your wishlist is empty</h2>
          <p>Add some books to your wishlist to see them here!</p>
          <Link to="/books" className="btn btn-primary">
            Browse Books
          </Link>
        </div>
      ) : (
        <div className="wishlist-grid">
          {wishlist.map((book) => (
            <div key={book._id} className="wishlist-item">
              <div className="wishlist-item-image">
                <img src={book.image} alt={book.title} />
                <div className="wishlist-item-overlay">
                  <button
                    className="btn btn-icon"
                    onClick={() => removeFromWishlist(book._id)}
                    title="Remove from wishlist"
                  >
                    <FaTrash />
                  </button>
                  <button
                    className="btn btn-icon"
                    onClick={() => addToCart(book)}
                    title="Add to cart"
                  >
                    <FaShoppingCart />
                  </button>
                </div>
              </div>
              <div className="wishlist-item-details">
                <h3>{book.title}</h3>
                <p className="author">by {book.author}</p>
                <p className="price">${book.price.toFixed(2)}</p>
                <Link to={`/books/${book._id}`} className="btn btn-outline">
                  View Details
                </Link>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Wishlist;
