import React, { useState } from 'react';
import axios from 'axios';
import { FaSpinner, FaSearch, FaBox, FaTruck, FaCheckCircle, FaTimesCircle } from 'react-icons/fa';
import './OrderTracking.css';

const OrderTracking = () => {
  const [trackingNumber, setTrackingNumber] = useState('');
  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!trackingNumber.trim()) return;

    setLoading(true);
    setError(null);
    setOrder(null);

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('Please log in to track your order');
      }

      const response = await axios.get(`http://localhost:5001/api/orders/track/${trackingNumber}`, {
        headers: {
          'x-auth-token': token
        }
      });

      setOrder(response.data);
    } catch (err) {
      console.error('Error tracking order:', err);
      setError(err.response?.data?.msg || err.message || 'Failed to track order');
    } finally {
      setLoading(false);
    }
  };

  const getStatusIcon = (status) => {
    switch (status.toLowerCase()) {
      case 'delivered':
        return <FaCheckCircle className="status-icon delivered" />;
      case 'shipped':
        return <FaTruck className="status-icon shipped" />;
      case 'cancelled':
        return <FaTimesCircle className="status-icon cancelled" />;
      default:
        return <FaBox className="status-icon processing" />;
    }
  };

  const formatAddress = (address) => {
    return `${address.address}, ${address.city}, ${address.state} ${address.zipCode}`;
  };

  return (
    <div className="tracking-container">
      <h1>Track Your Order</h1>
      
      <form onSubmit={handleSubmit} className="tracking-form">
        <div className="input-group">
          <input
            type="text"
            value={trackingNumber}
            onChange={(e) => setTrackingNumber(e.target.value)}
            placeholder="Enter tracking number"
            className="tracking-input"
          />
          <button type="submit" className="track-button" disabled={loading}>
            {loading ? <FaSpinner className="spinner" /> : <FaSearch />}
            Track
          </button>
        </div>
      </form>

      {error && (
        <div className="error-message">
          <p>{error}</p>
        </div>
      )}

      {order && (
        <div className="tracking-result">
          <div className="order-info">
            <h2>Order #{order._id.slice(-6)}</h2>
            <p className="order-date">
              Ordered on {new Date(order.createdAt).toLocaleDateString()}
            </p>
            <p className="tracking-number">
              Tracking Number: {order.trackingNumber}
            </p>
          </div>

          <div className="current-status">
            <h3>Current Status</h3>
            <div className="status-display">
              {getStatusIcon(order.status)}
              <span className={`status-text ${order.status}`}>
                {order.status.charAt(0).toUpperCase() + order.status.slice(1)}
              </span>
            </div>
          </div>

          <div className="tracking-timeline">
            <h3>Tracking History</h3>
            {order.trackingHistory.map((event, index) => (
              <div key={index} className="timeline-item">
                <div className="timeline-icon">
                  {getStatusIcon(event.status)}
                </div>
                <div className="timeline-content">
                  <h4>{event.status.charAt(0).toUpperCase() + event.status.slice(1)}</h4>
                  <p>{event.description}</p>
                  {event.location && <p className="location">Location: {event.location}</p>}
                  <span className="timeline-date">
                    {new Date(event.timestamp).toLocaleString()}
                  </span>
                </div>
              </div>
            ))}
          </div>

          <div className="shipping-info">
            <h3>Shipping Information</h3>
            <div className="info-grid">
              <div className="info-item">
                <label>Shipping Address:</label>
                <p>{formatAddress(order.shippingAddress)}</p>
              </div>
              <div className="info-item">
                <label>Recipient:</label>
                <p>{order.shippingAddress.fullName}</p>
              </div>
            </div>
          </div>

          <div className="order-items">
            <h3>Order Items</h3>
            <div className="items-list">
              {order.items.map((item, index) => (
                <div key={index} className="order-item">
                  <img src={item.book.image} alt={item.book.title} className="item-image" />
                  <div className="item-details">
                    <h4>{item.book.title}</h4>
                    <p>Quantity: {item.quantity}</p>
                    <p>Price: ${item.price.toFixed(2)}</p>
                  </div>
                </div>
              ))}
            </div>
            <div className="order-total">
              <h4>Total Amount: ${order.totalAmount.toFixed(2)}</h4>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default OrderTracking;
