import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FaStar, FaShoppingCart, FaArrowLeft } from 'react-icons/fa';
import './BookDetails.css';

const BookDetails = ({ addToCart }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [book, setBook] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    const fetchBookDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5001/api/books/${id}`);
        setBook(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to load book details. Please try again later.');
        setLoading(false);
      }
    };

    fetchBookDetails();
  }, [id]);

  const handleAddToCart = () => {
    if (book) {
      addToCart({ ...book, quantity });
      navigate('/cart');
    }
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="loading-spinner"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <p className="error-message">{error}</p>
        <button className="button" onClick={() => navigate('/books')}>
          Back to Books
        </button>
      </div>
    );
  }

  if (!book) {
    return null;
  }

  return (
    <div className="book-details">
      <button className="back-button" onClick={() => navigate(-1)}>
        <FaArrowLeft /> Back
      </button>

      <div className="book-details-container">
        <div className="book-image-container">
          <img src={book.image} alt={book.title} className="book-image" />
        </div>

        <div className="book-info">
          <h1 className="book-title">{book.title}</h1>
          <p className="book-author">by {book.author}</p>

          <div className="book-rating">
            {[...Array(5)].map((_, index) => (
              <FaStar
                key={index}
                className={index < book.rating ? 'star filled' : 'star'}
              />
            ))}
            <span className="rating-number">({book.rating})</span>
          </div>

          <p className="book-description">{book.description}</p>

          <div className="book-meta">
            <div className="meta-item">
              <span className="meta-label">Category:</span>
              <span className="meta-value">{book.category}</span>
            </div>
            <div className="meta-item">
              <span className="meta-label">Pages:</span>
              <span className="meta-value">{book.pages}</span>
            </div>
            <div className="meta-item">
              <span className="meta-label">Language:</span>
              <span className="meta-value">{book.language}</span>
            </div>
          </div>

          <div className="book-price">${book.price}</div>

          <div className="quantity-selector">
            <button
              className="quantity-button"
              onClick={() => setQuantity(Math.max(1, quantity - 1))}
            >
              -
            </button>
            <span className="quantity">{quantity}</span>
            <button
              className="quantity-button"
              onClick={() => setQuantity(quantity + 1)}
            >
              +
            </button>
          </div>

          <button className="add-to-cart-button" onClick={handleAddToCart}>
            <FaShoppingCart /> Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
};

export default BookDetails;
