import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { FaStar, FaStarHalfAlt, FaFilter, FaTimes, FaShoppingCart } from 'react-icons/fa';
import './Books.css';

const RatingStars = ({ rating }) => {
  const stars = [];
  const fullStars = Math.floor(rating);
  const hasHalfStar = rating % 1 !== 0;

  // Add full stars
  for (let i = 0; i < fullStars; i++) {
    stars.push(<FaStar key={`full-${i}`} className="star full" />);
  }

  // Add half star if needed
  if (hasHalfStar) {
    stars.push(<FaStarHalfAlt key="half" className="star half" />);
  }

  // Add empty stars
  const emptyStars = 5 - stars.length;
  for (let i = 0; i < emptyStars; i++) {
    stars.push(<FaStar key={`empty-${i}`} className="star empty" />);
  }

  return (
    <div className="rating-stars">
      {stars}
      <span className="rating-value">{rating.toFixed(1)}</span>
    </div>
  );
};

const Books = () => {
  const [books, setBooks] = useState([]);
  const [filteredBooks, setFilteredBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showFilters, setShowFilters] = useState(false);
  const [addingToCart, setAddingToCart] = useState({});
  const [filters, setFilters] = useState({
    category: '',
    minPrice: '',
    maxPrice: '',
    minRating: '',
    searchQuery: ''
  });

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/books');
        console.log('Fetched books:', response.data);
        setBooks(response.data);
        setFilteredBooks(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to load books. Please try again later.');
        setLoading(false);
      }
    };

    fetchBooks();
  }, []);

  useEffect(() => {
    let result = [...books];

    // Apply category filter
    if (filters.category) {
      result = result.filter(book => book.category === filters.category);
    }

    // Apply price range filter
    if (filters.minPrice) {
      result = result.filter(book => book.price >= parseFloat(filters.minPrice));
    }
    if (filters.maxPrice) {
      result = result.filter(book => book.price <= parseFloat(filters.maxPrice));
    }

    // Apply rating filter
    if (filters.minRating) {
      result = result.filter(book => book.averageRating >= parseFloat(filters.minRating));
    }

    // Apply search query
    if (filters.searchQuery) {
      const query = filters.searchQuery.toLowerCase();
      result = result.filter(
        book =>
          book.title.toLowerCase().includes(query) ||
          book.author.toLowerCase().includes(query) ||
          book.description.toLowerCase().includes(query)
      );
    }

    setFilteredBooks(result);
  }, [filters, books]);

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const clearFilters = () => {
    setFilters({
      category: '',
      minPrice: '',
      maxPrice: '',
      minRating: '',
      searchQuery: ''
    });
  };

  const categories = [...new Set(books.map(book => book.category))];

  const handleAddToCart = async (bookId) => {
    try {
      setAddingToCart(prev => ({ ...prev, [bookId]: true }));
      const token = localStorage.getItem('token');
      
      if (!token) {
        // Redirect to login if no token
        window.location.href = '/login';
        return;
      }

      console.log('Adding book to cart:', bookId);
      const response = await axios.post(
        'http://localhost:5001/api/cart',
        { bookId, quantity: 1 },
        {
          headers: {
            'x-auth-token': token,
            'Content-Type': 'application/json'
          }
        }
      );

      console.log('Add to cart response:', response.data);
      // Show success message or update UI as needed
    } catch (err) {
      console.error('Error adding to cart:', err);
      setError(err.response?.data?.message || 'Failed to add item to cart');
    } finally {
      setAddingToCart(prev => ({ ...prev, [bookId]: false }));
    }
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="loading-spinner"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <p className="error-message">{error}</p>
      </div>
    );
  }

  return (
    <div className="books-page">
      <div className="books-header">
        <h1>Our Books Collection</h1>
        <button 
          className="filter-toggle-button"
          onClick={() => setShowFilters(!showFilters)}
        >
          {showFilters ? <FaTimes /> : <FaFilter />}
          {showFilters ? 'Hide Filters' : 'Show Filters'}
        </button>
      </div>

      <div className={`filters-container ${showFilters ? 'show' : ''}`}>
        <div className="search-bar">
          <input
            type="text"
            name="searchQuery"
            value={filters.searchQuery}
            onChange={handleFilterChange}
            placeholder="Search books..."
            className="search-input"
          />
        </div>

        <div className="filters-grid">
          <div className="filter-group">
            <label>Category</label>
            <select
              name="category"
              value={filters.category}
              onChange={handleFilterChange}
              className="filter-select"
            >
              <option value="">All Categories</option>
              {categories.map(category => (
                <option key={category} value={category}>
                  {category}
                </option>
              ))}
            </select>
          </div>

          <div className="filter-group">
            <label>Price Range</label>
            <div className="price-inputs">
              <input
                type="number"
                name="minPrice"
                value={filters.minPrice}
                onChange={handleFilterChange}
                placeholder="Min"
                className="price-input"
              />
              <span>to</span>
              <input
                type="number"
                name="maxPrice"
                value={filters.maxPrice}
                onChange={handleFilterChange}
                placeholder="Max"
                className="price-input"
              />
            </div>
          </div>

          <div className="filter-group">
            <label>Minimum Rating</label>
            <select
              name="minRating"
              value={filters.minRating}
              onChange={handleFilterChange}
              className="filter-select"
            >
              <option value="">Any Rating</option>
              <option value="4">4+ Stars</option>
              <option value="3">3+ Stars</option>
              <option value="2">2+ Stars</option>
              <option value="1">1+ Stars</option>
            </select>
          </div>

          <button className="clear-filters-button" onClick={clearFilters}>
            Clear Filters
          </button>
        </div>
      </div>

      <div className="books-grid">
        {filteredBooks.length > 0 ? (
          filteredBooks.map(book => (
            <div key={book._id} className="book-card">
              <div className="book-image-container">
                <img src={book.image} alt={book.title} className="book-image" />
              </div>
              <div className="book-info">
                <h2 className="book-title">{book.title}</h2>
                <p className="book-author">by {book.author}</p>
                <div className="book-rating">
                  <RatingStars rating={book.averageRating || 0} />
                  <span className="total-ratings">
                    {book.totalRatings || 0} {book.totalRatings === 1 ? 'rating' : 'ratings'}
                  </span>
                </div>
                <p className="book-price">${book.price.toFixed(2)}</p>
                <div className="book-actions">
                  <Link to={`/books/${book._id}`} className="view-details-button">
                    View Details
                  </Link>
                  <button
                    className={`add-to-cart-button ${addingToCart[book._id] ? 'adding' : ''}`}
                    onClick={() => handleAddToCart(book._id)}
                    disabled={addingToCart[book._id]}
                  >
                    <FaShoppingCart />
                    {addingToCart[book._id] ? 'Adding...' : 'Add to Cart'}
                  </button>
                </div>
              </div>
            </div>
          ))
        ) : (
          <div className="no-books-found">
            <p>No books found matching your filters.</p>
            <button className="button" onClick={clearFilters}>
              Clear Filters
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Books;
