import React, { useState, useEffect } from 'react';
import { 
  BrowserRouter as Router, 
  Routes, 
  Route, 
  Navigate,
  createBrowserRouter,
  RouterProvider,
  Outlet
} from 'react-router-dom';
import axios from 'axios';
import { AuthProvider } from './context/AuthContext';
import { CartProvider } from './context/CartContext';
import { WishlistProvider } from './context/WishlistContext';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import Home from './pages/Home';
import Books from './pages/Books';
import BookDetails from './pages/BookDetails';
import Cart from './pages/Cart';
import Checkout from './pages/Checkout';
import Profile from './pages/Profile';
import Login from './pages/Login';
import Register from './pages/Register';
import OrderTracking from './pages/OrderTracking';
import OrderHistory from './pages/OrderHistory';
import Dashboard from './pages/admin/Dashboard';
import AdminBooks from './pages/admin/Books';
import AdminOrders from './pages/AdminOrders';
import AdminUsers from './pages/AdminUsers';
import NotFound from './pages/NotFound';
import Wishlist from './pages/Wishlist';
import About from './pages/About';
import Author from './pages/Author';
import './App.css';

// Layout component that includes Navbar and Footer
const Layout = ({ isAuthenticated, user, onLogout }) => {
  return (
    <div className="app">
      <Navbar 
        isAuthenticated={isAuthenticated}
        user={user}
        onLogout={onLogout}
      />
      <main className="main-content">
        <Outlet />
      </main>
      <Footer />
    </div>
  );
};

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const checkAuth = async () => {
      try {
        const token = localStorage.getItem('token');
        if (token) {
          // Set default auth header
          axios.defaults.headers.common['x-auth-token'] = token;
          
          // Verify token and get user data
          const response = await axios.get('http://localhost:5001/api/auth/user');
          setUser(response.data);
          setIsAuthenticated(true);
        }
      } catch (err) {
        console.error('Auth check error:', err);
        localStorage.removeItem('token');
        delete axios.defaults.headers.common['x-auth-token'];
      } finally {
        setLoading(false);
      }
    };

    checkAuth();
  }, []);

  const handleLogin = async (token) => {
    localStorage.setItem('token', token);
    axios.defaults.headers.common['x-auth-token'] = token;
    setIsAuthenticated(true);
    
    try {
      const response = await axios.get('http://localhost:5001/api/auth/user');
      setUser(response.data);
    } catch (err) {
      console.error('Error fetching user data:', err);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['x-auth-token'];
    setIsAuthenticated(false);
    setUser(null);
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="loading-spinner"></div>
      </div>
    );
  }

  const router = createBrowserRouter([
    {
      path: "/",
      element: <Layout isAuthenticated={isAuthenticated} user={user} onLogout={handleLogout} />,
      children: [
        {
          index: true,
          element: <Home />
        },
        {
          path: "books",
          element: <Books />
        },
        {
          path: "books/:id",
          element: <BookDetails />
        },
        {
          path: "cart",
          element: isAuthenticated ? <Cart /> : <Navigate to="/login" replace />
        },
        {
          path: "checkout",
          element: isAuthenticated ? <Checkout /> : <Navigate to="/login" replace />
        },
        {
          path: "profile",
          element: isAuthenticated ? <Profile user={user} /> : <Navigate to="/login" replace />
        },
        {
          path: "login",
          element: isAuthenticated ? <Navigate to="/profile" replace /> : <Login onLogin={handleLogin} />
        },
        {
          path: "register",
          element: isAuthenticated ? <Navigate to="/profile" replace /> : <Register onLogin={handleLogin} />
        },
        {
          path: "tracking",
          element: isAuthenticated ? <OrderTracking /> : <Navigate to="/login" replace />
        },
        {
          path: "orders",
          element: isAuthenticated ? <OrderHistory /> : <Navigate to="/login" replace />
        },
        {
          path: "admin",
          element: isAuthenticated && user?.role === 'admin' ? <Dashboard /> : <Navigate to="/login" replace />
        },
        {
          path: "admin/books",
          element: isAuthenticated && user?.role === 'admin' ? <AdminBooks /> : <Navigate to="/login" replace />
        },
        {
          path: "admin/orders",
          element: isAuthenticated && user?.role === 'admin' ? <AdminOrders /> : <Navigate to="/login" replace />
        },
        {
          path: "admin/users",
          element: isAuthenticated && user?.role === 'admin' ? <AdminUsers /> : <Navigate to="/login" replace />
        },
        {
          path: "wishlist",
          element: isAuthenticated ? <Wishlist /> : <Navigate to="/login" replace />
        },
        {
          path: "about",
          element: <About />
        },
        {
          path: "author/:authorName",
          element: <Author />
        },
        { path: '*', element: <NotFound /> }
      ]
    }
  ], {
    future: {
      v7_startTransition: true,
      v7_relativeSplatPath: true
    }
  });

  return (
    <AuthProvider>
      <CartProvider>
        <WishlistProvider>
          <RouterProvider router={router} />
        </WishlistProvider>
      </CartProvider>
    </AuthProvider>
  );
}

export default App;
